package lesson;

public class DoWhileStatements 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 0;
		
		while(a <= -1)
		{
			System.out.println(a); // print out nothing
			a++;
		}
		
		int b = 0;
		
		do {
			//System.out.println(b);
			b++;
		} while(b <= 10); // print first letter out if meet do-while requirements
	}

}
